import { useState } from "react"
import './forms.scss'
import { faTimesCircle, faEdit } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

const Settings = (props) => {




    var settingsCacheWarehouse = []
    var settingsCacheBrand = []
    var settingsCacheCategory = []
    var settingsCacheBank = []

    var WareHousePreviewData=[]

    var retrievedDataWareHouse = localStorage.getItem("WareHouse");
    if(retrievedDataWareHouse==null){
        WareHousePreviewData=[1,2,3]
    }
    else{
    WareHousePreviewData = JSON.parse(retrievedDataWareHouse);
    }
    var retrievedDataBrand = localStorage.getItem("Brand");
    var BrandPreviewData = JSON.parse(retrievedDataBrand);

    var retrievedDataCategory = localStorage.getItem("Category");
    var CategoryPreviewData = JSON.parse(retrievedDataCategory);

    var retrievedDataBank = localStorage.getItem("Bank");
    var BankPreviewData = JSON.parse(retrievedDataBank);

    const onDelete = (e, i) => {
        if (e.target.name == 'warehouse') {
            WareHousePreviewData.splice(i, 1);
            localStorage.setItem('WareHouse', JSON.stringify(WareHousePreviewData));

        }
        if (e.target.name == 'brand') {
            BrandPreviewData.splice(i, 1)
            localStorage.setItem('Brand', JSON.stringify(BrandPreviewData));
        }
        if (e.target.name == 'category') {
            CategoryPreviewData.splice(i, 1);
            localStorage.setItem('Category', JSON.stringify(CategoryPreviewData));

        }
        if (e.target.name == 'bank') {
            BankPreviewData.splice(i, 1)
            localStorage.setItem('Bank', JSON.stringify(BankPreviewData));
        }



    }


    const handleSubmit = (e) => {
        e.preventDefault();

        settingsCacheBrand.push(e.target.AddBrand.value)
        settingsCacheCategory.push(e.target.AddCategory.value)
        settingsCacheBank.push(e.target.AddBank.value)
        settingsCacheWarehouse.push(e.target.AddWareHouse.value)

        localStorage.setItem('Brand', JSON.stringify(settingsCacheBrand));
        localStorage.setItem('Category', JSON.stringify(settingsCacheCategory));
        localStorage.setItem('Bank', JSON.stringify(settingsCacheBank));
        localStorage.setItem('WareHouse', JSON.stringify(settingsCacheWarehouse));


    }




    return (
        <div>
            <section className='pos'>
                <h1 className="title">Edit Menus</h1>

                <form className="contact-form row" onSubmit={handleSubmit}>
                    <div className="form-field col-lg-12">
                        <a name="AddBrand" id="posName" className="input-text js-input"></a>
                        <label className="label" for="">Add New Brand</label>
                    </div>
                    <div className="form-field col-lg-12">
                        <a name="AddCategory" id="posName" className="input-text js-input"></a>
                        <label className="label" for="">Add New Category</label>
                    </div>
                    <div className="form-field col-lg-12">
                        <a name="AddWareHouse" id="posName" className="input-text js-input"></a>
                        <label className="label" for="">Add WareHouse</label>
                    </div>
                    <div className="form-field col-lg-12">
                        <a name="AddBank" id="posName" className="input-text js-input"></a>
                        <label className="label" for="">Add Bank Name</label>
                    </div>
                    <input className="submit-btn" type="submit" value="Save Settings" />
                </form>
            </section>

            <section>
                <table className="table table-hover">
                    <thead className="thead-dark">
                        <tr>
                            <th scope="col">#</th>
                            <th scope="col">Warehouse</th>
                            <th scope="col">Brands</th>
                            <th scope="col">Categories</th>
                            <th scope="col">Bank</th>
                        </tr>
                    </thead>
                    <tbody>
                        {WareHousePreviewData.map(i => {
                        if(WareHousePreviewData)
                            return (
                                <tr key={i}>
                                    <th scope="row">{i}</th>
                                    <td>{WareHousePreviewData[i]}<FontAwesomeIcon icon={faTimesCircle} style={{ color: "red" }} name="warehouse" id="closeButton" className="fas" onClick={() => onDelete(i)} /></td>
                                    <td>{BrandPreviewData[i]}<FontAwesomeIcon icon={faTimesCircle} style={{ color: "red" }} name="brand" id="closeButton" className="fas" onClick={() => onDelete(i)} /></td>
                                    <td>{CategoryPreviewData[i]}<FontAwesomeIcon icon={faTimesCircle} style={{ color: "red" }} name="category" id="closeButton" className="fas" onClick={() => onDelete(i)} /></td>
                                    <td>{BankPreviewData[i]}<FontAwesomeIcon icon={faTimesCircle} style={{ color: "red" }} id="closeButton" name="bank" className="fas" onClick={() => onDelete(i)} /></td>
                                </tr>

                            )
                        })}
                    </tbody>
                </table>


            </section>
        </div>
    )
}

export default Settings
