import React, { useState } from 'react';
import AddItemModal from './AddItemModal';
import './forms.scss'
import {connect} from 'react-redux';
import * as actions from '../actions/pos';
const cart=[];
const products = [];
const intialvalues={


  ItemSKU:'',
  ItemQuantity:1,
  ItemPrice:0,
}



var tableRowIndex = 0;
function  TableRow ({row, handleDataChange, deleteRow},props) {
    var price,quantity=0;
   const [active, setActive] = useState(0);
   const [filtered, setFiltered] = useState([]);
   const [isShow, setIsShow] = useState(false);
   const [input, setInput] = useState("");

   const onChange = e => {
     const  suggestions  = ["123","456"];
     const input = e.currentTarget.value;
     const newFilteredSuggestions = suggestions.filter(
       suggestion =>
         suggestion.toLowerCase().indexOf(input.toLowerCase()) > -1
     );
     setActive(0);
     setFiltered(newFilteredSuggestions);
     setIsShow(true);
     setInput(e.currentTarget.value)
     handleInputChange(e);
   };
 const onClick = e => {
     setActive(0);
     setFiltered([]);
     setIsShow(false);
     setInput(e.currentTarget.innerText)
   };
 const onKeyDown = e => {
     if (e.keyCode === 13) { 
       setActive(0);
       setIsShow(false);
       setInput(filtered[active])
     }
     else if (e.keyCode === 38) { 
       return (active === 0) ? null : setActive(active - 1);
     }
     else if (e.keyCode === 40) { 
       return (active - 1 === filtered.length) ? null : setActive(active + 1);
     }
   };
 const renderAutocomplete = () => {
     if (isShow && input) {
       if (filtered.length) {
         return (
           <ul className="autocomplete">
             {filtered.map((suggestion, index) => {
               let className;
               if (index === active) {
                 className = "active";
               }
               return (
                 <li className={className} key={suggestion} onClick={onClick}>
                   {suggestion}
                 </li>
               );
             })}
           </ul>
         );
       } else {
         return (
           <div className="no-autocomplete">
             <em>No product found</em>
           </div>
         );
       }
     }
     return <></>;
   }




   const handleSubmit =e=>{
    actions.TableString.StringOfTable="Bill_Child_Temp";

      setValues(values.ItemSKU=input)
      console.log(input)
      e.preventDefault();
      console.log(values);
      props.createPos(values,()=>{})
      window.alert("Product Added")

   }
   const [values,setValues] = useState(intialvalues);
   function handleInputChange(e){

     const {name,value}=e.target
     setValues({
       ...values,
       [name]: value

      })
      cart.push(values)


    }


   const index = row.index
   const [ItemQuantity, handleChangeItemQuantity] = useState(row.ItemQuantity);
   const [ItemSKU, handleChangeItemSKU] = useState(row.ItemSKU);
   const [ItemPrice, handleChangeItemPrice] = useState(row.ItemPrice);

   const updateValues = e => {
    var inputName = e.target.name
    var inputValue = e.target.value
      var ItemPrice = e.target.ItemPrice
      if(inputName == 'ItemQuantity'){
         handleChangeItemQuantity(inputValue)
      }else if(inputName == 'ItemSKU'){
         handleChangeItemSKU(inputValue)
      }
      else if(inputName == 'ItemPrice'){
        handleChangeItemPrice(inputValue)
     }

      handleDataChange({
         index: index,
         ItemQuantity: ItemQuantity,
         last_name: last_name
      })
   }

   const removeRow = () => {
      deleteRow(index)
   }

   return(

      <tr>
          <th scope="row">{index}</th>
      <td><div className="form-field col-lg-6">
      <input className="input-text js-input"
        type="text"
        onChange={onChange}
        onKeyDown={onKeyDown}
        name="ItemSKU"
        value={products[index]}
      />
      {renderAutocomplete()}

      </div>    
      </td>
      <td>
        <div className="form-field col-lg-6 ">
          <input name="ItemQuantity" onChange={updateValues} value={values.ItemQuantity}   id="ItemQuantity" className="input-text js-input" type="Number" required/>
        </div>
      </td>
      <td>
        <div className="form-field col-lg-6 ">
            <input name="ItemPrice" onChange={updateValues} value={values.ItemPrice}  id="Price" className="input-text js-input" type="Amount"  />
        </div>
      </td>
      <td>
        {values.ItemPrice*values.ItemQuantity}{" PKR"}
      </td>
      <td><button type="button" className="btn btn-remove" onClick={removeRow}>&times;</button></td>
      </tr>

   )
}

function Add(props) {
  const [active, setActive] = useState(0);
  const [filtered, setFiltered] = useState([]);
  const [isShow, setIsShow] = useState(false);
  const [input, setInput] = useState("");

  const onChange = e => {
    const  suggestions  = ["121241","143123","8471239"];
    const input = e.currentTarget.value;
