import React, {useState} from 'react';
import './forms.css'
import {connect} from 'react-redux';
import * as actions from '../actions/pos';
import * as Sales_preview from './Sales_preview';







function Sales(props) {


   const [active, setActive] = useState(0);
   const [filtered, setFiltered] = useState([]);
   const [isShow, setIsShow] = useState(false);
   const [input, setInput] = useState("");
   const [subtotal,setSubtotal] = useState("");

   const onChange = e => {
     const  suggestions  = ["123","456"];
     const input = e.currentTarget.value;
     const newFilteredSuggestions = suggestions.filter(
       suggestion =>
         suggestion.toLowerCase().indexOf(input.toLowerCase()) > -1
     );
     setActive(0);
     setFiltered(newFilteredSuggestions);
     setIsShow(true);
     setInput(e.currentTarget.value)
   };
 const onClick = e => {
     setActive(0);
     setFiltered([]);
     setIsShow(false);
     setInput(e.currentTarget.innerText)
   };
 const onKeyDown = e => {
     if (e.keyCode === 13) { 
       setActive(0);
       setIsShow(false);
       setInput(filtered[active])
     }
     else if (e.keyCode === 38) { 
       return (active === 0) ? null : setActive(active - 1);
     }
     else if (e.keyCode === 40) { 
       return (active - 1 === filtered.length) ? null : setActive(active + 1);
     }
   };
 const renderAutocomplete = () => {
     if (isShow && input) {
       if (filtered.length) {
         return (
           <ul className="autocomplete">
             {filtered.map((suggestion, index) => {
               let className;
               if (index === active) {
                 className = "active";
               }
               return (
                 <li className={className} key={suggestion} onClick={onClick}>
                   {suggestion}
                 </li>
               );
             })}
           </ul>
         );
       } else {
         return (
           <div className="no-autocomplete">
             <em>Not found</em>
           </div>
         );
       }
     }
     return <></>;
   }

   const intialvalues={

      ItemBrand:'Haier',
      ItemType:'AC',
      ItemSKU:'',
      ItemQuantity:'',
      ItemPrice:'',
   }
   const [values,setValues] = useState(intialvalues);

   const handleSubmit =e=>{
    actions.TableString.StringOfTable="Bill_Child_Temp";

      setValues(values.ItemSKU=input)
      console.log(input)
      e.preventDefault();
      console.log(values);
      props.createPos(values,()=>{})
      window.alert("Product Added")
      Sales_preview.reRender.reRender+=1

   }
   const handleInputChange=e=>{
      const {name,value}=e.target
      setValues({
         ...values,
         [name]: value
      })
   }



   return (
      <div className="container">

         <section className="pos">
   <h1 className="title">Point Of Sales Terminal</h1>
   <div className="row">
   <table class="table">
  <thead class="thead-light">
    <tr>
      <th scope="col">#</th>
      <th scope="col">Product</th>
      <th scope="col">Quantity</th>
      <th scope="col">Unit Price</th>
      <th scope="col">SubTotal</th>


    </tr>
  </thead>
  <tbody>
    <tr>
      <th scope="row">1</th>
      <td><div className="form-field col-lg-6">
      <input className="input-text js-input"
        type="text"
        onChange={onChange}
        onKeyDown={onKeyDown}
        value={input}
      />
      {renderAutocomplete()}

      </div>    </td>
      <td><div className="form-field col-lg-6 ">
         <input name="ItemQuantity" onChange={handleInputChange} value={values.ItemQuantity} id="ItemQuantity" className="input-text js-input" type="Number" required/>
      </div></td>
      <td><div className="form-field col-lg-6 ">
         <input name="ItemPrice" onChange={handleInputChange} value={values.ItemPrice} id="Price" className="input-text js-input" type="Amount"  />
      </div></td>
    </tr>
  </tbody>
</table>
     </div>
   <form className="contact-form row" onSubmit={handleSubmit}>














      <div className="form-field col-lg-12">
         <input  className="submit-btn" type="submit" value="Add Product"/>
      </div>

   </form>
</section>
      </div>
    );
   }


   const mapStateToProps = state=>({
      poslist: state.pos.list  
   })

   const mapActionToProps =  {
      createPos: actions.create,


   }

   export default connect(mapStateToProps,mapActionToProps)(Sales);
