import React, { useState } from 'react';

import './forms.scss'
import {connect} from 'react-redux';
import * as actions from '../actions/pos';
import * as Sales_preview from './Sales_preview';

const products = [];
var tableRowIndex = 0;
function TableRow ({row, handleDataChange, deleteRow},props) {
    var price,quantity=0;
   const [active, setActive] = useState(0);
   const [filtered, setFiltered] = useState([]);
   const [isShow, setIsShow] = useState(false);
   const [input, setInput] = useState("");

   const onChange = e => {
     const  suggestions  = ["123","456"];
     const input = e.currentTarget.value;
     const newFilteredSuggestions = suggestions.filter(
       suggestion =>
         suggestion.toLowerCase().indexOf(input.toLowerCase()) > -1
     );
     setActive(0);
     setFiltered(newFilteredSuggestions);
     setIsShow(true);
     setInput(e.currentTarget.value)
   };
 const onClick = e => {
     setActive(0);
     setFiltered([]);
     setIsShow(false);
     setInput(e.currentTarget.innerText)
   };
 const onKeyDown = e => {
     if (e.keyCode === 13) { 
       setActive(0);
       setIsShow(false);
       setInput(filtered[active])
     }
     else if (e.keyCode === 38) { 
       return (active === 0) ? null : setActive(active - 1);
     }
     else if (e.keyCode === 40) { 
       return (active - 1 === filtered.length) ? null : setActive(active + 1);
     }
   };
 const renderAutocomplete = () => {
     if (isShow && input) {
       if (filtered.length) {
         return (
           <ul className="autocomplete">
             {filtered.map((suggestion, index) => {
               let className;
               if (index === active) {
                 className = "active";
               }
               return (
                 <li className={className} key={suggestion} onClick={onClick}>
                   {suggestion}
                 </li>
               );
             })}
           </ul>
         );
       } else {
         return (
           <div className="no-autocomplete">
             <em>Not found</em>
           </div>
         );
       }
     }
     return <></>;
   }

   const intialvalues={

      ItemBrand:'Haier',
      ItemType:'AC',
      ItemSKU:'',
      ItemQuantity:0,
      ItemPrice:0,
   }

   const [values,setValues] = useState(intialvalues);

   const handleSubmit =e=>{
    actions.TableString.StringOfTable="Bill_Child_Temp";

      setValues(values.ItemSKU=input)
      console.log(input)
      e.preventDefault();
      console.log(values);
      props.createPos(values,()=>{})
      window.alert("Product Added")
      Sales_preview.reRender.reRender+=1

   }
   function handleInputChange(e){

     const {name,value}=e.target
     setValues({
       ...values,
       [name]: value

      })


    }


   const index = row.index
   const [first_name, handleChangeFirstName] = useState(row.first_name);
   const [last_name, handleChangeLastName] = useState(row.last_name);

   const updateValues = e => {
      var inputName = e.target.name
      var inputValue = e.target.value
      if(inputName == 'first_name'){
         handleChangeFirstName(inputValue)
      }else if(inputName == 'last_name'){
         handleChangeLastName(inputValue)
      }

      handleDataChange({
         index: index,
         first_name: first_name,
         last_name: last_name
      })
   }

   const removeRow = () => {
      deleteRow(index)
   }

   return(
      <tr>
          <th scope="row">{index}</th>
      <td><div className="form-field col-lg-6">
      <input className="input-text js-input"
        type="text"
        onChange={onChange}
        onKeyDown={onKeyDown}
        value={products[index]}
      />
      {renderAutocomplete()}

      </div>    </td>
      <td>
        <div className="form-field col-lg-6 ">
          <input name="ItemQuantity" onChange={handleInputChange} value={values.ItemQuantity}   id="ItemQuantity" className="input-text js-input" type="Number" required/>
        </div>
      </td>
      <td>
        <div className="form-field col-lg-6 ">
            <input name="ItemPrice" onChange={handleInputChange} value={values.ItemPrice}  id="Price" className="input-text js-input" type="Amount"  />
        </div>
      </td>
      <td>
        {values.ItemPrice*values.ItemQuantity}{" PKR"}
      </td>
      <td><button type="button" className="btn btn-remove" onClick={removeRow}>&times;</button></td>
      </tr>

   )
}

function Add(props) {
   const [talbeRows, setRows] = useState([{
         index: 0,
         first_name: "",
         last_name: ""
      }
   ]);

   // Receive data from TableRow 
   const handleChange = data => {
      talbeRows[data.index] = data
   }

   // Add New Table Row
   const addNewRow = () => {
      tableRowIndex = parseFloat(tableRowIndex) + 1
      var updatedRows = [...talbeRows]
      updatedRows[tableRowIndex] = {index: tableRowIndex, first_name: "", last_name: ""}
      setRows(updatedRows)
   }

   // Remove Table row if rows are count is more than 1
   const deleteRow = (index) => {
      if(talbeRows.length > 1){
         var updatedRows = [...talbeRows]
         var indexToRemove = updatedRows.findIndex(x => x.index == index);
         if(indexToRemove > -1){
            updatedRows.splice(indexToRemove, 1)
            setRows(updatedRows);
         }
      }
   }

   const handleKeyDown = (event,index) => {
    if (event.key === 'Enter') {
      products.push(event.target.value)
      addNewRow();
      event.target.value="";

    }
   }
   const intialvalues={

    ItemBrand:'Haier',
    ItemType:'AC',
    ItemSKU:'',
    ItemQuantity:0,
    ItemPrice:0,
 }
   const [values,setValues] = useState(intialvalues);


   const handleSubmit =e=>{
    actions.TableString.StringOfTable="Bill_Child_Temp";

      e.preventDefault();
      console.log(values);
      props.createPos(values,()=>{})
      window.alert("Product Added")
      Sales_preview.reRender.reRender+=1

   }

  return (
    <div className="container">
                 <section className="pos">
                 <h1 className="title">Point Of Sales Terminal</h1>
   <div className="row">



<div className="search__container">
    <input name="product"  className="search__input" type="text" placeholder="Enter Product Name" onKeyDown={handleKeyDown}/>
</div>

       <table className="table" id="customers">
       <thead className="thead-light">
    <tr>
      <th scope="col">#</th>
      <th scope="col">Product</th>
      <th scope="col">Quantity</th>
      <th scope="col">Unit Price</th>
      <th scope="col">SubTotal</th>
      <th scope="col">Delete</th>


    </tr>
  </thead>
          <tbody>
               {
                  talbeRows.map((row, index) => {
                     if(row)
                     return(
                        <TableRow key={index} row={row} handleDataChange={handleChange} deleteRow={deleteRow}></TableRow>
                     )
                  })
               }
          </tbody>
       </table>
       <table className="table table-condensed table-bordered table-striped">
						<tbody><tr>
							<td>
								<div className="pull-right">
								<b>Items:</b> 
								<span className="total_quantity">2.00</span>

								<b>Total: </b>
									<span className="price_total">6.00</span>
								</div>
							</td>
						</tr>
					</tbody></table>
                    <div className="form-field col-lg-12">
         <input  className="btn btn-primary" type="submit" value="Add Products"/>
      </div>


       </div>


       </section>
    </div>
  );
}

export default Add;
