import jsPDFInvoiceTemplate,{jsPDF,OutputType}from "jspdf-invoice-template";
import './forms.css'



function PrintInvoice(){
    const pdfObject = jsPDFInvoiceTemplate(props);


var props = {
    outputType: OutputType.Save,
    returnJsPDFDocObject: true,
    fileName: "Invoice 2021",
    orientationLandscape: false,
    logo: {
        src: "https://raw.githubusercontent.com/edisonneza/jspdf-invoice-template/demo/images/logo.png",
        width: 53.33, //aspect ratio = width/height
        height: 26.66,
        margin: {
            top: 0, //negative or positive num, from the current position
            left: 0 //negative or positive num, from the current position
        }
    },
    business: {
        name: "Home Electronics",
        address: "Gulistan-e-Jauhar",
        phone: "03112286006",
        email: "mazzasimq1@gmail.com",
        website: "masssive.com",
    },
    contact: {
        label: "Invoice issued for:",
        name: "Maaz Asim",
        address: "Sana Avenue",
        phone: "03112286006",
    },
    invoice: {
        label: "Invoice #: ",
        num: 1,
        invDate: "Payment Date: 06/12/2021 6:48",

        headerBorder: false,
        tableBodyBorder: false,
        header: ["#", "Description", "Price", "Quantity", "Unit", "Total"],
        table: Array.from(Array(10), (item, index)=>([
            index + 1,
            "There are many variations ",
            200.5,
            4.5,
            "m2",
            400.5
        ])),
        invTotalLabel: "Total:",
        invTotal: "145,250.50",
        invCurrency: "ALL",
        row1: {
            col1: 'VAT:',
            col2: '20',
            col3: '%',
            style: {
                fontSize: 10 //optional, default 12
            }
        },
        row2: {
            col1: 'SubTotal:',
            col2: '116,199.90',
            col3: 'ALL',
            style: {
                fontSize: 10 //optional, default 12
            }
        },
        invDescLabel: "Invoice Note",
        invDesc: "There are many variations of passages of Lorem Ipsum available, but the majority have suffered alteration in some form, by injected humour, or randomised words which don't look even slightly believable. If you are going to use a passage of Lorem Ipsum, you need to be sure there isn't anything embarrassing hidden in the middle of text. All the Lorem Ipsum generators on the Internet tend to repeat predefined chunks as necessary.",
    },
    footer: {
        text: "The invoice is created on a computer and is valid without the signature and stamp.",
    },
    pageEnable: true,
    pageLabel: "Page ",
};

    var pdfcreated=pdfObject.jsPDFDocObject;
    const printinvoice= pdfcreated =>{pdfcreated.autoPrint()}


return(

<div className="form-field col-lg-12">
         <input  className="submit-btn" type="button" value="Print Invoice" onClick={printinvoice}/>
</div>    
);
}
export default PrintInvoice;
