import jsPDFInvoiceTemplate,{OutputType}from "jspdf-invoice-template";
import React, {useState} from 'react';
import {useEffect} from "react";
import {connect} from 'react-redux';
import * as actions from '../actions/pos';
import './forms.css'



function PrintInvoice(props){

    useEffect(() => {
        actions.TableString.StringOfTable="Bill_Child_Temp";
        console.log('Updating Sales_preview')
        props.fetchAllPos();
    }, []);

    const temp=props.poslist.pop().map((record)=>{record.CustomerName=CustomerName,record.CustomerAddress=CustomerAddress});


    var props = {
        outputType: OutputType.Save,
        returnJsPDFDocObject: true,
        fileName: "Invoice 2021",
        orientationLandscape: false,
        logo: {
            src: "",
            width: 53.33, //aspect ratio = width/height
            height: 26.66,
            margin: {
                top: 0, //negative or positive num, from the current position
                left: 0 //negative or positive num, from the current position
            }
        },
        business: {
            name: "Home Electronics",
            address: "Gulistan-e-Jauhar",
            phone: "03112286006",
            email: "mazzasimq1@gmail.com",
            website: "masssive.com",
        },
        contact: {
            label: "Invoice issued for:",       
            name: temp.CustomerName,
            address: temp.CustomerAddress,
            phone: temp.CustomerPhoneNumber,
        },
        invoice: {
            label: "Invoice #: ",
            num: temp.BillMasterId,
            invDate: temp.BillCreatedOn,

            headerBorder: false,
            tableBodyBorder: false,
            header: ["#", "Description", "Price", "Quantity", "Total"],
            table: Array.from(Array(1), (item, index)=>([
                index + 1,
            "Haier Refrigenerator SKU:1128937 ",
            10000,
            4,
            40000
        ])),
        invTotalLabel: "Total:",
        invTotal: "40,000",
        invCurrency: "PKR",


        invDescLabel: "Invoice Note",
        invDesc: "I AM JUST TESTING THIS COMPONENT AT THIS TIME TESTER:MAAZ ASIM",
    },
    footer: {
        text: "The invoice is created on a computer and is valid without the signature and stamp.",
    },
    pageEnable: true,
    pageLabel: "Page ",
};

function printInvoiceOnClick () {
const pdfObject=jsPDFInvoiceTemplate(props).jsPDFDocObject;

    pdfObject.autoPrint();
    pdfObject.output('dataurlnewwindow');

}

return(

<div className="form-field col-lg-12">
         <input  className="submit-btn" type="button" value="Print Invoice" onClick={printInvoiceOnClick}/>
</div>    
);
}
const mapStateToProps = state=>({
    poslist: state.pos.list  
})

const mapActionToProps =  {
fetchAllPos: actions.fetchall
}

export default connect(mapStateToProps,mapActionToProps)(PrintInvoice);
