import api from './api';

const Tables={
    BillChild_POS:"BillChild_POS/",
    BillMaster_POS:"BillMaster_POS/",
    EmployeeRecord:"EmployeeRecord/",
    ItemDetails:"ItemDetails/",
    StockDetails:"StockDetails/",
    StockTransfer:"StockTransfer",
    UserLogin:"UserLogin/",
    UserRights:"UserRights/",
    UserTypeRecord:"UserTypeRecord/",
    WareHouse:"WareHouse/"
    }

    const baseurl = "http://localhost:5000/api/";

export var TableString={StringOfTable:""};

export const ACTION_TYPES = {
    CREATE:'CREATE',
    UPDATE:'UPDATE',
    DELETE :'DELETE',
    FETCH_ALL:'FETCH_ALL'
}

export const fetchall = (baseurl,Table) => dispatch => {
    api.pos(baseurl,Table).fetchAll()
    console.log(baseurl+Table)

    .then(response => {
            console.log(response)
            dispatch({
                type:ACTION_TYPES.FETCH_ALL,
                payload:response.data
            })

        }
    )
    .catch(err=>console.log(err))

}
export const create = (data,onSuccess,Table=TableString.StringOfTable) => dispatch => {

    api.pos(baseurl,Table).create(data)
    console.log(baseurl+Table)
    .then(response=>{
        dispatch({
            type:ACTION_TYPES.CREATE,
            payload:response.data
        })
        onSuccess()
    })
    .catch(err=>console.log(err))
}

export const update = (data,id,onSuccess,Table) => dispatch => {
    api.pos(baseurl,Table).update(id,data,Table)
    .then(response=>{
        dispatch({
            type:ACTION_TYPES.UPDATE,
            payload:{id,...data}
        })
        onSuccess()
    })
    .catch(err=>console.log(err))
}

export const Delete = (id,onSuccess,Table) => dispatch => {
    api.pos(baseurl,Table).delete(id)
    .then(response=>{
        dispatch({
            type:ACTION_TYPES.DELETE,
            payload:id 
        })
        onSuccess()
    })
    .catch(err=>console.log(err))
}
