        actions.TableString.StringOfTable="ItemDetailsContoller";
        console.log('Updating Sales_preview')
        props.fetchAllPos();
    }, []);


    return(
        <div className="table1" style={{"background":"transparent"}}>
            <table className="list-group" key={index} style={{"background":"transparent"}}>
                <tr>
                    <th>Location</th>
                    <th>SKU</th>
                    <th>Quantity</th>
                    <th>Purchase Price</th>
                </tr>
            {console.log(props.poslist)}
        {props.poslist.map((record, index) => {
            return (
            <tr className="list-group-item d-flex justify-content-between align-items-center" style={{"background":"transparent"}}>
              {record.ItemBrand}{" "}
                {record.ItemType}{" "}
                {record.ItemSKU}{" "}
                {"Quantity : "}{record.ItemQuantity}{" "}
                <span className="badge bg-primary rounded-pill">{record.ItemQuantity * record.ItemPrice}{" PKR"}</span>
                </tr>
           );
        })}
        </table>
        </div>
    );
}


const mapStateToProps = state=>({
        poslist: state.pos.list  
})

const mapActionToProps =  {
    fetchAllPos: actions.fetchall
}

export default connect(mapStateToProps,mapActionToProps)(SalesPreview);
