import * as React from "react";
import Login from "./Login"


const loginArray=  {
      "User":"MaazAsim",
      "Pass":"pokemon123"
  }


const authContext = React.createContext();

function LoginAuth(props) {
  console.log(this.props.frmdetails.Password);
  const [authed, setAuthed] = React.useState(false);

  return {
    authed,
    login() {
      return new Promise((res) => {
        console.log(props.frmdetails.Password)
        if(props.frmdetails.Password==loginArray.Pass && props.frmdetails.User==loginArray.User)
        {
          setAuthed(true);
        }

        res();
      });
    },
    logout() {
      return new Promise((res) => {
        setAuthed(false);
        res();
      });
    }
  };
}

export function AuthProvider({ children }) {
  const auth = LoginAuth();

  return (
    <authContext.Provider value={auth}>
      {children}
    </authContext.Provider>
  );
}

export default function AuthConsumer() {
  return React.useContext(authContext);
}
