import React, {useState} from 'react';
import {useEffect} from "react";
import './Sales.css'
import {connect} from 'react-redux';
import * as actions from '../actions/pos';
import './Sales_preview.css'








function Sales_preview(props){
    const [render,setRender]=useState(false);
    setRender(this.props.dataParentToCild);

    useEffect(() => {
        props.fetchAllPos();
    }, [render]);

    return(
        <div>
        {props.poslist.map((record, index) => {
            return (<ul className="list-group" key={index}>
            <li className="list-group-item d-flex justify-content-between align-items-center">
              {record.ItemBrand}{" "}
                {record.ItemType}{" "}
                {record.ItemSKU}{" "}
                {record.ItemQuantity}{" "}
                <span className="badge bg-primary rounded-pill">{record.ItemQuantity * record.ItemPrice}</span>
                </li>
            </ul>);
        })}
        </div>
    );
}


actions.TableString.StringOfTable="BillChild_POS";
const mapStateToProps = state=>({
        poslist: state.pos.list  
})

const mapActionToProps =  {
    fetchAllPos: actions.fetchall
}

export default connect(mapStateToProps,mapActionToProps)(Sales_preview);
