import React, {useState} from 'react';
import {useEffect} from "react";
import './Sales.css'
import {connect} from 'react-redux';
import * as actions from '../actions/pos';
import './Sales_preview.css'








function Sales_preview(props){
    useEffect(() => {
        props.fetchAllPos();
    }, []);

    return(
        <>


        <ul class="list-group">
  <li className="list-group-item d-flex justify-content-between align-items-center">
  {props.poslist.map((record, index) => {
            return (<p id="Preview" key={index}>
                {record.ItemBrand}
                {record.ItemType}
                {record.ItemSKU}
                {record.ItemPrice}
                <span className="badge bg-primary rounded-pill">{record.ItemQuantity}</span>
            </p>);
        })}
  </li>
  <li className="list-group-item d-flex justify-content-between align-items-center">
    A second list item
    <span className="badge bg-primary rounded-pill">2</span>
  </li>
  <li className="list-group-item d-flex justify-content-between align-items-center">
    A third list item
    <span className="badge bg-primary rounded-pill">1</span>
  </li>
</ul>
</>
    );
}


actions.TableString.StringOfTable="BillMaster_POS";
const mapStateToProps = state=>({
        poslist: state.pos.list  
})

const mapActionToProps =  {
    fetchAllPos: actions.fetchall
}

export default connect(mapStateToProps,mapActionToProps)(Sales_preview);
