import './SideNav.css';
import React ,{useEffect,useState} from 'react'
import {Outlet,useNavigate} from 'react-router-dom'
import Login from '../Login/Login';

const SideNav = () => {

    var [date,setDate] = useState(new Date());

    useEffect(() => {
        var timer = setInterval(()=>setDate(new Date()), 1000 )
        return function cleanup() {
            clearInterval(timer)
        }

    });


    const navigate=useNavigate();

   function handleClick(path){
     navigate(path);
   }

    function refreshforlogout(){
        window.location.reload(false);
    }



        // const [token,setToken]=useState();
        // if(!token) {
        //     return <Login setToken={setToken} />
        //   }


        const days = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];
        var today = new Date();

        var date = days[today.getDay()]+','+today.getDate()+'-'+(today.getMonth()+1)+'-'+today.getFullYear();

   return (

     <div  >
         <div className="topnav">
  <a class="active" href="#home">Home</a>
  <a href="#contact">Contact</a>
  <a href="#about">{((today.getHours() < 10)?"0":"") + today.getHours() +":"+ ((today.getMinutes() < 10)?"0":"") + today.getMinutes() +":"+ ((today.getSeconds() < 10)?"0":"") + today.getSeconds()}</a>
  <a href="#news">{date}</a>
</div>
   <div className="area"></div><nav className="main-menu">
            <ul>
                <li>
                    <a className="nameofbrand">
                        <i className="fa fa-home fa-2x"></i>
                        <span className="nav-text">
                            Home Electronics
                        </span>
                    </a>

                </li>
                <li className="has-subnav">
                    <a onClick={() => handleClick("Salesparent")}>
                        <i className="fa fa-cart-plus fa-2x"></i>
                        <span className="nav-text">
                            Point Of Sales
                        </span>
                    </a>

                </li>
                <li className="has-subnav">
                    <a  onClick={() => handleClick("Warehouse")}>
                        <i className="fas fa-warehouse fa-2x"></i>
                        <span className="nav-text">
                            Warehouse Management
                        </span>
                    </a>

                </li>






                <li className="has-subnav">
                    <a  onClick={() => handleClick("ItemDetails")}>
                       <i className="fas fa-tv fa-2x"></i>
                        <span className="nav-text">
                            Item Management
                        </span>
                    </a>

                </li>
            </ul>

            <ul className="logout">
                <li>
                   <a onClick={refreshforlogout}>
                         <i className="fa fa-power-off fa-2x"></i>
                        <span className="nav-text">
                            Logout
                        </span>
                    </a>
                </li>  
            </ul>
        </nav>
        <Outlet />
 </div>

   )
 }
 export default SideNav;
