import { ACTION_TYPES } from "../actions/pos";

    const initialState = {
        list:[]
    }

    export const pos = (state = initialState, action) => {
        switch (action.type) {
            case ACTION_TYPES.FETCH_ALL:
                return{
                    ...state,
                    list:[...action.payload]
                }


            default:
                return state;
        }
    }
