import './SideNav.scss';
import React from 'react'
import {Link,Outlet,useNavigate} from 'react-router-dom'


const SideNav = () => {
   const [startAnimate, setStartAnimate] = React.useState(false);
   const [highlightTopPosition, setStateHighlightTopPosition] = React.useState(2);
   const [currCount, setCurrCount] = React.useState(0);

   const onClickTab = (count) => {
     setStartAnimate(false);
     setCurrCount(count);
     setStateHighlightTopPosition(count * 52);

     setTimeout(() => {
       setStartAnimate(true);
     }, 100);
   }

   React.useEffect(() => {

     setTimeout(() => {
       setStartAnimate(true);
     }, 500);



     return () => {

     }
   }, []);
   const navigate=useNavigate();

   function handleClick(path) {
    navigate(path);
  }

   return (

     <div className="container">
         <div className="sidebar">
           <div style={{ top: `${highlightTopPosition}px` }} className={`sidebar__highlight ${startAnimate && 'sidebar__highlight__animate'}`}></div>

           <a className={currCount === 0 && 'active'}  onClick={() => handleClick("Salesparent")}>
             <span className={currCount === 0 && 'text-active'}><i className="fas fa-arrow-right"></i>POS</span>
           </a>
          <a className={currCount === 1 && 'active'}  onClick={() => onClickTab(1)}>
             <span className={currCount === 1 && 'text-active'}><i className="fas fa-arrow-right"></i> WareHouse</span>
           </a>
          <a className={currCount === 2 && 'active'}  onClick={() => onClickTab(2)}>
             <span className={currCount === 2 && 'text-active'}><i className="fas fa-arrow-right"></i> Dashboard</span>
           </a>
           <a className={currCount === 3 && 'active'}  onClick={() => onClickTab(3)}>
             <span className={currCount === 3 && 'text-active'}><i className="fas fa-arrow-right"></i> Salary</span>
           </a>
         </div>
       </div>

   )
 }
 export default SideNav;
